<?php// Test PHP Email script by Nate Baldwin, www.mindpalette.comunset($email);unset($message);$errors = "";$formStatus = (isset($HTTP_POST_VARS['formStatus'])) ? $HTTP_POST_VARS['formStatus'] : "";if ($formStatus == "submitted") {	$email = (isset($HTTP_POST_VARS['email'])) ? $HTTP_POST_VARS['email'] : "";	if ($email == "") $errors .= "Please enter your email address before submitting form.<br>\n";	$emailPattern = "^[A-Z0-9._-]+@[A-Z0-9._-]+\.[A-Z]{2,4}$";	if ($email != "" AND !eregi($emailPattern, $email)) 		$errors .= "The email address entered does not to be a valid address for testing.<br>\n";	if ($errors == "") {		$subject = "PHP Email Test";		$message = "If you receive this email, your server has successfully sent an email using PHP.\n";		@$mailStatus = mail($email, $subject, $message);		if (!$mailStatus) $errors .= "Server error - PHP has not been configured to send out emails yet, sorry.<br>\n";		}	}?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html>	<head>		<meta http-equiv="content-type" content="text/html;charset=iso-8859-1">		<meta name="generator" content="Adobe GoLive">		<title>Test PHP Email Configuration</title>		<style type="text/css" media="screen"><!--body, div, td, p  { color: black; font-size: 13px; font-family: Verdana, Arial, Helvetica, sans-serif; background-color: #c8c8c8 }a { color: #00417d; font-size: 13px; font-family: Verdana, Arial, Helvetica, sans-serif; text-decoration: none }a:link { color: #00417d; font-size: 13px; font-family: Verdana, Arial, Helvetica, sans-serif; text-decoration: none }a:visited { color: #00417d; font-size: 13px; font-family: Verdana, Arial, Helvetica, sans-serif; text-decoration: none }a:hover { color: #0064ff; font-size: 13px; font-family: Verdana, Arial, Helvetica, sans-serif; text-decoration: underline }.error { color: #c80019 }.success { color: #00417d }h1   { color: #00417d; font-size: 32px; font-family: "Times New Roman", Georgia, Times; font-weight